import React, { useState } from 'react'
import {useDispatch} from 'react-redux'
import {useNavigate} from 'react-router-dom'
import {Avatar,Button, Paper, Grid, Typography, Container, TextField, Icon} from '@material-ui/core'
import LockOutlineIcon from '@material-ui/icons/LockOutlined'
import useStyles from './styles'
import Input from './Input'
//谷歌登录组件
import {GoogleLogin} from 'react-google-login'
import icon from './icon'
import Google_log from './Google_log'
import { AUTH } from '../../constants/actionTypes'
import { signin, signup } from '../../actions/auth'
const Auth = () => {
  const classes = useStyles()
  const initialState={ firstName:'', lastName:'', email:'', password: '', confirmPassword: '' };
  
  const [formData, setFormData]=useState(initialState)
  const [showPassword, setShowPassword] = useState(false);
  const [isSignup,setIsSignup]= useState(false);
  const  dispatch=useDispatch()
  const navigate=useNavigate()
  const handleShowPassword=()=> setShowPassword((prevShowPassword)=>!prevShowPassword);
  
  const switchMode =() =>  {
    setIsSignup((prevIsSignup)=>!prevIsSignup) 
    //无论如何都设成密码不可见
    setShowPassword(false)
    }
    const googleSuccess= async (res) =>{
      console.log(`Gogle登录成功啦啦啦`)
      console.log(res)
      

      
    }
    const googleError = (error) =>{
      console.log('google登录失败啦,error信息：')
      console.log(error)
    }
    

  const handleSubmit=async(e)=>{
      e.preventDefault();
      
      if(isSignup){
        dispatch(signup(formData,navigate))
      }else{
        dispatch(signin(formData,navigate))
      }
  }
  const handleChange =(e)=>{
    setFormData({...formData, [e.target.name] : e.target.value})

  }


  return (
    <Container component="main" maxWidth="xs">
      <Paper className={classes.paper} elevation={3}>
        <Avatar className={classes.avatar}>
          <LockOutlineIcon/>
        </Avatar>
        <Typography component="h1" variant="h5">{ isSignup ? 'Sign up' : 'Sign in' }</Typography>
        <form className={classes.form} onSubmit={handleSubmit}>
            <Grid container spacing={2}>
                {
                  isSignup && (
                    <> 
                      
                    
                        <Input name="firstName"  label="First Name" handleChange={handleChange} autoFocus half/>
                        <Input name="lastName"  label="Last Name" handleChange={handleChange} autoFocus half/>
                        
                      
                    </>
                  )
                }

              <Input name="email" label="Email Address" handleChange={handleChange} type="email" />
              <Input name="password" label="Password" handleChange={handleChange} type={showPassword ? 'text' : 'password'} handleShowPassword={handleShowPassword} />
              {isSignup && <Input name="confirmPassword" label="Repeat Password" handleChange={handleChange}/>}
            
            </Grid>

            
            
          <Button type="submit" fullWidth variant="contained" color="primary" className={classes.submit}>
            {isSignup ? 'Sign Up' : 'Sign In'}
          </Button>
             {/* <GoogleLogin
                clientId="20521068448-qc63tjr0ub065rv0knnl9gr1818744t4.apps.googleusercontent.com"
                render={(renderProps)=>(
                  <Button 
                  className={classes.googleButton} 
                  color='primary' fullWidth 
                  onClick={renderProps.onClick} 
                  disabled={renderProps.disabled} 
                  startIcon={<Icon/>} 
                  variant="contained">
                    Google Sign In
                  </Button>
                )}
                onSuccess={googleSuccess}
                onFailure={goodleFailure}
                cookiePolicy="single_host_origin"
            /> */}
            <Google_log/>
          <Grid container justify="flex-end">
                  <Button onClick={switchMode}>
                    {isSignup ? 'Already have an account? Sign In ': "Don't have an account ? Sign up"}
                  </Button>
          </Grid>
        </form>
      </Paper>

    </Container>
  )
}

export default Auth
